internal class AAXBatchBundleUIBuilder extends SysOperationAutomaticUIBuilder
{

    DialogField dialogFieldRecordCount;
    DialogField dialogFieldBundleSize;

    const int constantRecordCount = 1000;
    const int constanstBundleSize = 50;


}

Method.Source:
/// <summary>
/// This method runs before the dialog is created
/// </summary>
public void postBuild()
{
    super();


    // Retrieve the dialog fields
    dialogFieldRecordCount.value(constantRecordCount);
    dialogFieldBundleSize.value(constanstBundleSize);

}

public void getFromDialog()
{
    int counter = constantRecordCount;
    int bundleSize = constanstBundleSize;

    if(dialogFieldRecordCount)
    {
        counter = dialogFieldRecordCount.value();

    }

    if(dialogFieldBundleSize)
    {
        bundleSize = dialogFieldBundleSize.value();
    }

    AAXBatchBundleWorkItemTable::reInit(counter);






}
